mapper.select:
select
   p.id, p.headimage_id headImageId, i.src headImage, p.biaoti, p.jianjie, p.field
from wx.project p left join wx.img i on i.id = p.headimage_id


        and id = #{id}


        and headimage_id = #{headImageId}


        and biaoti = #{biaoti}


        and jianjie = #{jianjie}


        and field = #{field}
